    {name:'jessica',age:20,belt:"blue", id:2},
    {name:'silas',age:23,belt:"green",id:3},
  ]
  }

  addNinja = (ninja) =>{
console.log(ninja);
  }

  render() {
    return (
      <div className="App">
        <h1>this is good</h1>
        <Ninjas ninjas = {this.state.ninjas} />
          <AddNinja addNinja={this.addNinja} />
      </div>
    );
  }

}

export default App;
